import { Iauthor } from './author.model';

@Component({
  selector: 'app-authorlist',
  templateUrl: './authorlist.component.html',
  styleUrls: ['./authorlist.component.css']
})
export class AuthorlistComponent {
  @Input() book!: Iauthor;
}


<div class="booklist">
