import { Component, Input } from '@angular/core';
import { Ibook } from './book.model';
import { BookService } from './book.service';
@Component({
  selector: 'app-library',
  templateUrl: './library.component.html',
  styleUrls: ['./library.component.css']
})
export class LibraryComponent {
  books:any;

  @Input() titleFilter : string = ''; 
  @Input() priceFilter: number | null = null;
  @Input() authorFilter : string = '';

  onFilterChange(title: string, author: string, price: string) {
    this.titleFilter = title;
    this.authorFilter = author;
    this.priceFilter = price ? Number(price) : null;
    this.fetchBooks();
  }



  constructor(
    private bookSvc : BookService
  ){}

  ngOnInit()
  {
    this.fetchBooks();
  }

  fetchBooks() {
  this.bookSvc.getBooks(
    1,
    10,
    this.titleFilter,
    this.priceFilter !== null ? this.priceFilter : undefined,
    this.authorFilter
  ).subscribe({
    next: (books) => { this.books = books; },
    error: (err) => 
      { 
        console.error('Book API error:', err); 
        this.books = []; 
    }
  });
}

  onSearch()
  {
    this.fetchBooks();
  }

  getBookList() {
  return this.books || [];
}

}
